class Solution {
public:
    int m1 = 1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> marked(n+1,-1);
int mx=0;
for(int i=0;i<requirements.size();i++){
    vector<int> temp=requirements[i];
    marked[temp[0]+1]=temp[1];
    mx=max(mx,temp[1]);
}
vector<vector<int>> dp(n+1,vector<int>(mx+1));
for(int i=0;i<=mx;i++){
  if(i==0) dp[0][i]=1;
  else dp[0][i]=0;
}
for(int i=1;i<=n;i++){
  if(marked[i]==-1){
     for(int j=0;j<=mx;j++){
       dp[i][j]=0;
       for(int k=0;k<=min(j,i-1);k++){
        dp[i][j]=(dp[i][j]+dp[i-1][j-k])%m1;
       }
     }
  }else{
    int j=marked[i];
       dp[i][j]=0;
       for(int k=0;k<=min(j,i-1);k++){
        dp[i][j]=(dp[i][j]+dp[i-1][j-k])%m1;
       }     
  }
}
return dp[n][marked[n]];
    }
};
