class Solution {

public:

const int mod = (int)1e9+7;

 const int m = 400;

    int numberOfPermutations(int n, vector<vector<int>>& pp) {



        map<int, int>map;

        for (auto& a : pp) {

            map[a[0] + 1] = a[1];

        }

        vector<vector<long long int>> pq(n + 1, vector<long long int>( m + 1, 0));

        pq[0][0] = 1;

        for (int l = 1; l <= n; l++) {

            for (int i = 0; i <= m; i++) {

                for (int p = 0; p < l; p++) {

                    int pi = i - p;

                    if (pi >= 0) {

                        pq[l][i] = (pq[l][i] + pq[l - 1][pi]) %mod;

                    }

                }

            }

            if (map.count(l)) {

                int ti = map[l];

                for (int i= 0; i <= m; i++) {

                    if (i != ti) {

                        pq[l][i] = 0;

                    }

                }

            }

        }

        long long int ans = 0;

        for (auto lm: pq[n]) {

            ans = (ans + lm) % mod;

        }

        return ans*1LL;

    }

};
