class Solution {
        int m=1e9+7;
        int max_in=400;
public:
    int numberOfPermutations(int n, vector<vector<int>>& r ) {
        unordered_map<int,int>mp;
        vector<vector<long long >>pc(n+1,vector<long long>(401,0));
        for(auto it:r)
        {
            mp[it[0]+1]=it[1];            
        }
        pc[0][0]=1;
        int co=0;
        for(int i=0;i<n;i++)
        {
            co++;
        }
        for(int i=1;i<=n;i++)
        {
            for(int j=0;j<401;j++)
            {
                for(int k=0;k<i;k++)
                {
                    int temp=j-k;
                    if(temp>=0)
                    {
                        pc[i][j]=(pc[i][j]+pc[i-1][temp])%m;
                    }
                }
            }
            if(mp.count(i))
            {
                int te=mp[i];
                for(int h=0;h<=400;h++)
                {
                    if(te!=h) pc[i][h]=0;
                }
            }
        }
        int count=0;
        long long re=0;
        for(int g=0;g<401;g++)
        {
            count++;
            re=(re+pc[n][g])%m;
        }
        sort(r.begin(),r.end());
        if(count>=0&&co>=0) return re;
        return -1; 
    }
};
