class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int maxi=400;
        vector<vector<int>> per(n+1,vector<int>(maxi+1,0));
        map<int,int> mp;
        for(auto r:req){
            mp[r[0]+1]=r[1];
        }
        per[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int in=0;in<=maxi;in++){
                for(int npos=0;npos<i;npos++){
                    int prev=in-npos;
                    if(prev>=0){
                        per[i][in]=(per[i][in]+per[i-1][prev])%mod;
                    }
                }
            }
            if(mp.count(i)>0){
                int tar=mp[i];
                for(int in=0;in<=maxi;in++){
                    if(in!=tar){
                        per[i][in]=0;
                    }
                }
            }
        }
        ll res=0;
        for(auto it:per[n]){
            res=(res+it)%mod;
        }
        return (int) res;

    }
};
