class Solution {
public:
    int mod = 1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        map<int,int> mp;
        for(auto it:req)
            mp[it[0]] = it[1];
        vector<vector<int>> dp(n+1,vector<int>(401,0));
        dp[0][0] = 1;

        for(int ind = 1;ind <= n;ind++)
        {
            for(int j = 0;j <= 400;j++)
            {
                for(int pind = 0;pind < ind;pind++)
                {
                    if(j-pind >= 0)
                    {
                        dp[ind][j] = (dp[ind][j] + dp[ind-1][j-pind])%mod;
                    }
                }

                if(mp.find(ind-1) != mp.end())
                {
                    int need = mp[ind-1];
                    for(int j = 0;j <= 400;j++)
                    {
                        if(j != need)
                            dp[ind][j] = 0;
                    }
                }
            }
        }
        int ans = 0;
        for(int j = 0;j <= 400;j++)
        {
            ans = (ans + dp[n][j])%mod;
        }
        return ans;
    }
};
