int R = 401;
long long int MOD = 1e9+7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& rq) {
        vector<vector<long long>> dp (n+1,vector<long long>(R+1,0));
        dp[0][0] = 1;
        long long soln=0;
        unordered_map<int,int> mp ;
        for(auto r : rq){
            mp[r[0]+1] = r[1];
        }
        int i,j,k;
        for(i=1;i<=n;i++){
            for(j=0;j<=R;j++){
                for(k=0;k<i;k++){
                    if(j-k>=0) dp[i][j] = (dp[i][j]+dp[i-1][j-k])%MOD;
                }
            }
            int cnt = mp.count(i);
            if(cnt){
                for(int x=0;x<=R;x++){
                    if(x!=mp[i]){
                        dp[i][x]= 0;
                    }
                }
            }
        }
        for(long long ans : dp[n]){
            soln = (soln+ans)%MOD;
        }
        return soln;
    }
};
