class Solution {
public:
    int mod = (int)1e9 + 7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int inv = min(401,n*(n-1)/2+1);
        vector<vector<int>> dp(n+1,vector<int>(inv+1,0));

        vector<int> mp(n+1,-1);
        for(auto& req:requirements){
            mp[req[0]+1] = req[1];
        }

        dp[0][0] = 1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=inv;j++){
                for(int k=0;k<i;k++){
                    if(j-k>=0){
                        (dp[i][j] += dp[i-1][j-k])%=mod;
                    }
                }
            }
            if(mp[i]!=-1){
                for(int j=0;j<=inv;j++){
                    if(j!=mp[i]){
                        dp[i][j] = 0;
                    }
                }
            }
        }

        return dp[n][mp[n]];
    }
};
