from collections import defaultdict as dd
class Solution:
    def numberOfPermutations(self, n: int, r: List[List[int]]) -> int:
        mm=10**9+7
        d=dd(int)
        for i in r:
            d[i[0]+1]=i[1]

        p=[[0]*(400+1) for _ in range(n+1)]
        p[0][0]=1

        for i in range(1,n+1):
            for j in range(400+1):
                for k in range(i):
                    x=j-k
                    if x>=0:
                        p[i][j]=(p[i][j]+p[i-1][x])%mm
            if i in d:
                y=d[i]
                for j in range(400+1):
                    if j!=y:
                        p[i][j]=0
        return sum(p[n])%mm
