class Solution {
public:
    int maxi =0;
    int endval = 0;
    map<int,int>m;
    int dp[302][402];
    int mod = 1e9 +7;
    int f(int ind , int val){
        if(val > 400)return 0;
        if(ind== maxi){
            if(val == endval)return 1;
            else return 0;
        }
        if(m.count(ind)){
            if(m[ind]!= val)return 0;
        }
        if(dp[ind][val]!= -1)return dp[ind][val];
        int ans =0;
        for(int i=0 ;i<= ind ;i++){
            ans += f(ind + 1 , val + i );
            ans%=mod;
        }
        return dp[ind][val] = ans;
    }


    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        for(auto v: requirements){
            m[v[0] + 1]= v[1];
            if(v[0] + 1>maxi ){
                maxi = v[0] + 1;
                endval = v[1];
            }
        }
        memset(dp , -1 ,sizeof(dp));
        return f(0,0);
    }
};
