class Solution {
public:
    int mod=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        vector<vector<int>>arr(n+1,vector<int>(401,0));
        arr[0][0]=1;

        map<int,int>mp;

        for(auto it:requirements){
            mp.insert({it[0]+1,it[1]});
        }
        for(int i=1;i<=n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<i;k++){
                if(j-k>=0){
                    arr[i][j]=(arr[i][j]%mod+arr[i-1][j-k]%mod)%mod;
            }
                }
        }

            if(mp.find(i)!=mp.end()){
                for(int k=0;k<=400;k++){
                    if(k!=mp[i])arr[i][k]=0;
            }
            }
        }

        int ans=0;

        for(auto it:arr[n]){
            ans=(ans%mod+it%mod);
        }
        return ans;

    }
};
