#define ll long long int

class Solution {
public:


    ll dp[505][505];
    ll req[505]; 
    ll n;
    ll mod = 1e9 + 7;

    ll recur(int i , ll tl)
    {
        if(i == n)   return 1;
        if(tl > 405) return 0;
        if(dp[i][tl] != -1) return dp[i][tl];

        ll pre = i;
        ll ans = 0;

        if(req[i] == -1)
        {
            for(int j = 0 ; j <= pre ; j++)
            {
                ans += recur(i+1 , tl + j);
                if(ans >= mod) ans -= mod;
            }
        }
        else
        {
            for(int j = 0 ; j <= pre ; j++)
            {
                if(tl + j == req[i])
                {
                    ans += recur(i+1 , tl + j);
                    if(ans >= mod) ans -= mod;
                }
            }
        }

        return dp[i][tl] = ans;

    }

    int numberOfPermutations(int _n, vector<vector<int>>& requirements) {

        n = _n;

        for(int i = 0 ; i <= n ; i ++)
        {
            req[i] = -1;
        }

        for(auto k : requirements)
        {
            req[k[0]] = k[1];
        }

        for(int i = 0 ; i <= n ; i ++)
        {
            for(int j = 0 ; j <= max(405ll , n) ; j ++)
            {
                dp[i][j] = -1; 
            }
        }


        return recur(0,0);

    }
};
