#define ll long long
const int MOD = 1e9+7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<ll, ll> mp;
        for (auto &it : requirements) {
            ll chk=it[0]+1;
            mp[chk] = it[1];
        }
        vector<vector<ll>> v(n + 1, vector<ll>(401, 0));
        v[0][0] = 1;

        for (ll ii = 1; ii <= n; ii++) {
            for (ll jj = 0; jj <= 400; jj++) {
                for (ll kk = 0; kk < ii; kk++) {
                    ll p = jj - kk;
                    if (p >= 0) {
                        v[ii][jj] = (v[ii][jj] + v[ii - 1][p]) % MOD;
                    }
                }
            }
            if (mp.count(ii)) {
                ll tar = mp[ii];
                for (ll jj = 0; jj <= 400; jj++) {
                    if (jj != tar) {
                        v[ii][jj] = 0;
                    }
                }
            }
        }
        ll ans = 0;
        for (ll &it : v[n]) {
            ans = (ans + it) % MOD;
        }

        return ans;
    }
};
