#include <bits/stdc++.h>
using namespace std;
#define mod 1000000007
using ll = long long;

class Solution
{

public:
    int numberOfPermutations(int n, vector<vector<int>> &requirements)
    {
        ll res = 0;

        unordered_map<int, int> mp;
        for (auto &x : requirements)
        {
            int idx = x[0] + 1;
            int val = x[1];
            mp[idx] = val;
        }

        vector<vector<ll>> pcnt(n + 1, vector<ll>(400 + 1, 0));
        pcnt[0][0] = 1;

        for (int i = 1; i <= n; i++)
        {
            for (int j = 0; j <= 400; j++)
            {
                for (int nextPos = 0; nextPos < i; nextPos++)
                {
                    int lastInv = j;
                    lastInv -= nextPos;
                    if (lastInv >= 0)
                    {
                        ll curval = pcnt[i][j] + pcnt[i - 1][lastInv];
                        pcnt[i][j] = curval % mod;
                    }
                }
            }

            if (mp.find(i) != mp.end())
            {
                int getInv = mp[i];
                for (int k = 0; k <= 400; k++)
                {
                    if (k != getInv)
                    {
                        pcnt[i][k] = 0;
                    }
                }
            }
        }

        for (auto &cnt : pcnt[n])
        {
            res = (res + cnt) % mod;
        }
        int ans = res;

        return ans;
    }
};
