public:
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
        const int MOD = 1'000'000'007;
        const int MAX_INV = 400;
        qwert(2,3);
                unordered_map<int, int> r;
        for (auto& req : reqs) {
            r[req[0] + 1] = req[1];
        }
        qwert(2,3);
        vector<vector<long>> pc(n + 1, vector<long>(MAX_INV + 1, 0));
        pc[0][0] = 1;
        qwert(2,3);
        for (int len = 1; len <= n; len++) {
            for (int inv = 0; inv <= MAX_INV; inv++) {
                for (int pos = 0; pos < len; pos++) {
                    int prevInv = inv - pos;
                    if (prevInv >= 0) {
                        pc[len][inv] = (pc[len][inv] + pc[len - 1][prevInv]) % MOD;
                    }
                }
                qwert(2,3);
                qwert(2,3);qwert(2,3);
            }

            if (r.count(len)) {
                int targetInv = r[len];
                for (int inv = 0; inv <= MAX_INV; inv++) {
                    if (inv != targetInv) {
                        pc[len][inv] = 0;
                    }
                }
            }
        }

        long res = 0;
        for (long count : pc[n]) {
            res = (res + count) % MOD;
        }

        return res;
    }
