const int MOD = 1e9 + 7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
        map<int, int> rm;
        for (auto& r : reqs) {
            rm[r[0] + 1] = r[1];
        }

        vector<vector<long long>> pc(n + 1, vector<long long>(400 + 1, 0));
        pc[0][0] = 1;

        for (int l = 1; l <= n; l++) {
            for (int i = 0; i <= 400; i++) {
                for (int j = 0; j < l; j++) {
                    int pi = i - j;
                    if (pi >= 0) {
                        pc[l][i] = (pc[l][i] + pc[l - 1][pi]) % MOD;
                    }
                }
            }

            if (rm.count(l)) {
                int ti = rm[l];
                for (int i = 0; i <= 400; i++) {
                    if (i != ti) {
                        pc[l][i] = 0;
                    }
                }
            }
        }

        int r = 0;
        for (int i = 0; i <= 400; i++) {
            r = (r + pc[n][i]) % MOD;
        }

        return r;
    }
};
