class Solution {
public:

    const int MOD = 1e9 + 7;


    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        long long ans = 0;

        map<int,int> m;
       // int n = requirements.size();
        int m1 = requirements[0].size();

//         for(int i=0;i<n;i++){
//             auto it = requirements[i];
//             int a = it[0]+1;
//             int b = it[1];

//             m[a] = b;
//         }

        for(auto it: requirements){
            int a = it[0]+1;
            int b = it[1];
            m[a] = b;
        }

        int len = 405;
        int dp[305][405] = {0};
        dp[0][0] = 1;

        for(int i=1;i<=n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<i;k++){
                    if(j-k>=0){
                        dp[i][j] = (dp[i][j]%MOD + dp[i-1][j-k]%MOD);
                        dp[i][j] = dp[i][j]%MOD;
                    }
                }
            }

            if(m.find(i)!=m.end()){
                // int t = m[i];
                for(int p=0;p<=400;p++){
                    if(p!=m[i]){
                        dp[i][p] = 0;
                    }
                }
            }
        }

        for(auto it:dp[n]){
            ans = (ans%MOD + it%MOD);
            ans = ans%MOD;
        }

        return ans;





    }
};
