class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        const int MOD = 1e9 + 7;
        unordered_map<int, int> m;
        for (auto req : requirements) {
            m[req[0] + 1] = req[1];
        }
        vector<vector<long long>> v(n + 1, vector(401, 0LL));
        v[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int pos = 0; pos < i; pos++) {
                    int ct = j - pos;
                    if (ct >= 0) v[i][j] = v[i][j] + v[i - 1][ct] % MOD;
                }
            }
            if (m.contains(i)) {
                int t = m[i];
                for (int j = 0; j <= 400; j++) {
                    if (j != t) v[i][j] = 0;
                }
            }
        }
        long long res = 0;
        for (auto ct : v[n]) {
            res += ct;
            res %= MOD;
        }
        return res;

    }
};
