int MOD = 1e9+7;
const int M = 405;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req)
    {
        ll cache[n+2][M];
        memset(cache,0LL,sizeof(cache));
        cache[0][0]=1;

        int op = 0,sz = (int)req.size();
        sort(all(req),[&](vector<int> a,vector<int> b){
            return a[0] < b[0];
        } );

        ff(i,1,n+1)
        {
            ff(j,0,M-2)
            {
                ff(k,0,i)
                {
                    int op = j-i+k+1;
                    if( op >= 0)
                    {
                        cache[i][j] = (cache[i][j] %MOD + cache[i-1][op]%MOD ) %MOD;
                    }
                }
            }

            if(i==req[op][0]+1)
            {
                for(int j=0;j<M-2;j++)
                {
                    if(j!=req[op][1]) cache[i][j]=0;
                }
                op++;
                if(op == sz) break;
            }
        }

        ll x = cache[n][ req[sz-1][1] ];
        int ans = x%MOD;
        return ans;
    }
};
