class Solution {
public:
    int mod = 1e9+7;
    #define ll long long 
    ll dp[402][402]; 

    ll solve(int n, int k , vector<int> &arr){


        if(arr[n]!=-1){
            if(arr[n]!=k)
            return 0; 
        }

        if(n==0)
        return 0; 

        if(k == 0 and n == 1)
        return 1;

        if(dp[n][k]!=-1) return dp[n][k]; 


        ll ans = 0; 

        for(int i = 0; i <= k; ++i){ 
            if(i > n-1)continue; 
             ll res = solve(n-1, k-i , arr)%mod; 
             ans += res; 
             ans %= mod;
        } 

        return dp[n][k] = ans;
    } 


    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        vector<int> arr(n+1 , -1); 
        memset(dp, -1, sizeof dp); 

        for(auto req: requirements){
            arr[req[0]+1] = req[1];
        } 

        int k = arr[n]; 
        int res = solve(n, k , arr)%mod; 

        return res;
    }
};
