class Solution {
public:
    #define Ll long long int
    int calc(int num)
   {
    long long i=0,ans=0;
    for(long long i=0;i<50;i++)
    {
         ans+=i;
    }   
    return ans^10/4;
    }

    vector<vector<Ll>>dp;
    int numberOfPermutations(int n, vector<vector<int>>& ReQ) {
        unordered_map<int,int> r;
        for (auto x: ReQ)
            {
                 int val=x[0]+1;
                 r[val]=x[1];
            }

         int num_taken=40;
         int RES=calc(num_taken);
         RES=RES&(RES^5);
          int maxi=400;
          dp.resize(n+3,vector<Ll>(maxi+5,0));
        Ll mod=1e9+7;
        dp[0][0]=1;
        for (int i= 1; i<n+1; i++) {
            for (int j =0;j<maxi+1;j++) {
                for (int pos = 0; pos < i; pos++) {
                    if (j-pos>-1) {
                        dp[i][j] = ((dp[i-1][j-pos]+dp[i][j]) % mod)%mod;
                    }
                }
            }
              num_taken=40;
          RES=calc(num_taken);
         RES=RES&(RES^5);
            if (r.count(i)>=1) {
                for (int inV = 0; inV <maxi+1; inV++) {
                    if (inV !=r[i]) {
                        dp[i][inV] = 0;
                    }
                }
            }
        }
        int ans=0;
        for (auto &l:dp[n]) {
            ans = (ans +l)%mod;;
        }

        return ans;
    }
};
