class Solution:
    def numberOfPermutations(self, N, A) -> int:
        MOD = 10 ** 9 + 7
        A.sort()

        Adex = [None] * (N+1)
        for e,c in A:
            Adex[e+1] = c

        @cache
        def dp(i, invs):
            if i == N:
                if Adex[N] is not None and Adex[N] != invs: return 0
                return 1
            if invs > 400: return 0
            if Adex[i] is not None:
                if Adex[i] != invs: return 0

            nn = i + 1
            ans = 0
            for choice in range(nn):
                ans += dp(i + 1, invs + choice)
                ans %= MOD
            return ans

        return dp(0, 0)
