#define ll long long
#define MOD 1000000007
class Solution
{
public:
    int numberOfPermutations(int n, vector<vector<int>> &reqs)
    {
        map<int, int> mp; ll res = 0;
        for (auto &req : reqs) mp[req[0] + 1] = req[1];
        vector<vector<ll>> dp(n + 1, vector<ll>(401, 0)); dp[0][0] = 1;
        for (int i = 1; i < n + 1; ++i)
        {
            for (int inv = 0; inv < 401; ++inv)
            {
                for (int j = 0; j < i; ++j)
                {
                    if (inv - j < 0) continue;
                    else
                    {
                        dp[i][inv] = (dp[i][inv] % MOD + dp[i - 1][inv - j] % MOD) % MOD;
                    }
                }
            }
            if (mp.count(i) > 0)
            {
                for (int inv = 0; inv < 401; ++inv)
                {
                    if (inv == mp[i]) continue;
                    else dp[i][inv] = 0;
                }
            }
        }
        for (int inv = 0; inv < 401; ++inv)
        {
            res = (res % MOD + dp[n][inv] % MOD) % MOD;
        }
        return res;
    }
};
