class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
         int mod = 1e9+7;
        int ma = 400;
        vector<vector<long>> v(n + 1, vector<long>(ma + 1, 0));
        unordered_map<int, int> m;
        for (auto& it : reqs)
        {
            m[it[0] + 1] = it[1];
        }
        v[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= ma; j++) {
                for (int k = 0; k < i; k++) {
                    int prev = j - k;
                    if (prev >= 0) {
                        v[i][j] = (v[i][j] + v[i - 1][prev]) % mod;
                    }
                }
            }
            if (m.count(i)) {
                int target = m[i];
                for (int inv = 0; inv <= ma; inv++) {
                    if (inv != target) {
                        v[i][inv] = 0;
                    }
                }
            }
        }
        long ans= 0;
        for (long count : v[n]) {
            ans= (ans + count) % mod;
        }
        return ans;
    }
};
