const int modulo = 1000000007;
const int N = 300;
const int M = 401;
int dp[N][M + 1];

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> prefices(n, -1);
        for (const auto& p : requirements) {
            prefices[p[0]] = p[1];
        }

        if (prefices[0] > 0) {
            return 0;
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j <= M; ++j) {
                dp[i][j] = 0;
            }
        }
        dp[0][0] = 1;

        for (int i = 0; i + 1 < n; ++i) {
            for (int j = 0; j <= M; ++j) {
                for (int k = 0; k <= i + 1; ++k) {
                    int l = std::min(j + k, M);
                    if (prefices[i + 1] == -1 || l == prefices[i + 1])
                    {
                        dp[i + 1][l] = (dp[i][j] + dp[i + 1][l]) % modulo;
                    }
                }
            }
        }

        int ret = 0;
        for (int j = 0; j <= M; ++j) {
            ret = (ret + dp[n - 1][j]) % modulo;
        }

        return ret;
    }
};
