typedef long long ll;

class Solution {
public:
    int numberOfPermutations(int inn, vector<vector<int>>& arr) {
        const ll kmod = 1e9+7;
        const ll iv = 400;
        unordered_map<ll, ll> mpp;
        for (auto it : arr) {
            mpp[it[0] + 1] = it[1];
        }
        vector<vector<ll>> xv(inn + 1, vector<ll>(iv + 1, 0));
        xv[0][0] = 1;
        for (int ls = 1; ls <= inn; ls++) {
            for (int vs = 0; vs <= iv; vs++) {
                for (int p = 0; p < ls; p++) {
                    int pi = vs - p;
                    if (pi >= 0) 
                        xv[ls][vs] = (xv[ls][vs] + xv[ls - 1][pi]) % kmod;
                }
            }
            if (mpp.find(ls) != mpp.end()) {
                ll targetInv = mpp[ls];
                for (int q = 0; q <= iv; q++) 
                    if (q != targetInv) 
                        xv[ls][q] = 0;
            }
        }
        ll ans = 0;
        for (ll it : xv[inn]) 
            ans = (ans + it) % kmod;

        return ans;
    }
};
