class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int MOD=1e9+7;
        unordered_map<int, int> mp;
        for(auto &it: requirements) mp[it[0]+1]=it[1];
        vector<vector<long long>> dp(n+1, vector<long long> (401, 0));
        dp[0][0]=1;
        for(int i=1; i<=n; i++){
            for(int j=0; j<=400; j++)
                for(int k=0; k<i; k++)
                    if(j-k>=0) dp[i][j]=(dp[i][j]+dp[i-1][j-k])%MOD;
            if(mp.find(i)!=mp.end())
                for(int j=0; j<=400; j++)
                    if(j!=mp[i]) dp[i][j]=0;
        } long long ans=0;
        for(auto &it: dp[n])
            ans=(ans+it)%MOD;
        return (int)ans;
    }
};
