class Solution {
public:
    int mod = 1e9+7 ;
    int numberOfPermutations(int n, vector<vector<int>> &requirements) 
    {
        map <int,int> mp ;
        for(auto it : requirements) mp[it[0]+1] = it[1] ;
        int ans = 0 ;
        sort(requirements.begin(),requirements.end()) ;
        vector < vector <int> > dp(n+1,vector<int>(401,0)) ;
        dp[0][0] = 1 ;
        for(int i = 1 ; i<=n ; i++)
        {
            for(int j = 0 ; j<=400 ; j++)
            {
                for(int k = 0 ; k<i ; k++)
                {
                    int prev = j - k ;
                    if(prev>=0) dp[i][j] = (dp[i][j] + dp[i-1][prev])%mod ;
                }
            }
            if(mp.find(i) != mp.end()) for(int j = 0 ; j<=400 ; j++) if(j != mp[i]) dp[i][j] = 0 ;
        }
        for(int i = 0 ; i<=400 ; i++) ans = (ans + dp[n][i])%mod ;
        return ans ;
    }
};
