class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& R) {
        int per = 400;
        vector<vector<long long>> dp(n + 1, vector<long long>(per + 1, 0));
        dp[0][0] = 1;

        unordered_map<int, int> mp;
        for(auto x: R) 
            mp[x[0]] = x[1];

        for (int i = 1; i <= n; i++) {
            for (int k = 0; k <= per; k++)
                for (int j = 0; j < i; ++j) 
                    if (k >= j) 
                        dp[i][k] = (dp[i][k] + dp[i - 1][k - j])  % (int)(1e9 +7);

            if(mp.count(i-1)) {
                 for (int k = 0; k <= per; k++)
                     if(k != mp[i-1]) dp[i][k] = 0;
            }
        }
        int ans = 0;
        for(int  x: dp[n]) 
            ans = (ans+x) % (int)(1e9 +7);

        return ans;

    }
};
