class Solution {
public:
    int MOD = 1000000007, t[307];
    int dp[307][407];
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        for (int i=0;i<=n;i++)
        {
            t[i] = -1;
        }
        for (int i=0;i<req.size();i++)
        {
            t[req[i][0]+1] = req[i][1];
        }
        dp[0][0] = 1;
        for (int i=1;i<=n;i++)
        {
            for (int j=0;j<=400;j++)
            {
                for (int k=0;k<i;k++)
                {
                    int prev = j-k;
                    if (prev>=0)
                    {
                        dp[i][j] = (dp[i][j] + dp[i-1][prev])%MOD;
                    }
                }
            }
            if (t[i]!=-1)
            {
                for (int j=0;j<=400;j++)
                {
                    if (j!=t[i]) dp[i][j] = 0;
                }
            }
        }
        int res = 0;
        for (int i=0;i<=400;i++)
        {
            res = (res+dp[n][i]) % MOD;
        }
        return res;
    }
};
