class Solution
{
public:
    int numberOfPermutations(int n, vector<vector<int>> &v)
    {
        int n1 = 400;
        int f = 1000000007;
        int e1 = n1 + 1;
        int e2 = n + 1;
        vector<vector<long long>> v1(e2, vector<long long>(e1, 0));
        v1[0][0] = 1;
        unordered_set<int> st;
        vector<int> v2(n + 1, -1);
        for (auto it : v)
        {
            int x = it[0] + 1;
            v2[x] = it[1];
            st.insert(x);
        }
        for (int p1 = 1; p1 <= e2 - 1; p1++)
        {
            for (int p2 = 0; p2 <= e1 - 1; p2++)
            {
                for (int p3 = 0; p3 < p1; p3++)
                {
                    if (p2 - p3 < 0)
                        continue;
                    else
                    {
                        int x = v1[p1][p2];
                        int h = p1 - 1;
                        int p = p2 - p3;
                        int y = v1[h][p];
                        int z = x + y;
                        v1[p1][p2] = z % f;
                    }
                }
            }
            if (st.find(p1) == st.end())
                continue;
            else
            {
                int q = v2[p1];
                for (int p2 = 0; p2 <= e1 - 1; p2 += 1)
                {
                    if (p2 == q)
                        continue;
                    else
                        v1[p1][p2] = 0;
                }
            }
        }
        long w = 0;
        for (auto it : v1[n])
        {
            int h1 = w + it;
            w = h1 % f;
        }
        int u = w;
        return u;
    }
};
