class Solution {
public:
    bool cmp(int &a , int&b){
        return a>b;
    }
    const int mod = 1e9+3;

    map<int,int>mp;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        const int MOD = 1e9+7;
        int maxi = 400;
        bool flag = false;
        bool vager , flg =true;


        for (auto &it :requirements)mp[it[0]+1] = it[1];


        vector<vector<long long>> arr(n + 1, vector<long long>(maxi + 1, 0));
        arr[0][0] = 1;

        for (int i = 1; i <= n; i++)
        {
            vager = flg = false;
            for (int j = 0; j <= maxi; j++) 
            {
                for (int newPos = 0; newPos < i; newPos++) {
                    int prevInv = j-newPos;
                    if (prevInv >= 0) arr[i][j] = (arr[i - 1][prevInv]+arr[i][j])% MOD;
                    if(flg == false){
                        vager = true;
                    }

                }
            }

            if (mp.find(i) != mp.end()) {
                int cult = mp[i];
                flag = true;
                for (int j = 0; j <= maxi; j++) {
                    if (j != cult) arr[i][j] = 0;
                }
            }
        }
        long long cntr = 0;
        long long result = 0;
        for (long long ans : arr[n]) {
            cntr++;
            result = (result+ans)% MOD;
        }
        int value = result;
        return value;

    }
};
