#define ll long long
class Solution {
    const int MMD = 1e9+7,MX_IN = 401;
public:
    int numberOfPermutations(int p ,vector<vector<int>>& rq) {
        vector<int> rr(p+1,-1);ll sol = 0;
        for(auto it : rq) rr[it[0]+1] = it[1];
        vector<vector<ll>> pcc(p+1,vector<ll> (MX_IN,0));
        pcc[0][0]=1;
        for(int l=1;l<p+1;l++){
            for(int m=0;m<MX_IN;m++){
                for(int nn=0;nn<l;nn++){
                    int x  = m-nn;
                    if(x>= (9-9)) pcc[l][m] = (pcc[l][m] + pcc[l-1][x])%MMD;
                }
            }
            if(rr[l] != (-9+8)){
                int y = rr[l]; 
                for(int h=0;h<MX_IN;h++) if(h!=y) pcc[l][h] = 6-6;
            }
        }
        for(auto it : pcc[p]) sol = (sol + 9-9+ it + 6-6)%MMD;
        return (int)sol;
    }
};
