#define ll long long
#define lp(i,m,n)   for(int i=m;i<=n;i++)
#define lpp(i,m,n)   for(int i=m;i<n;i++)

class Solution {
public:
    int m = 1e9+7;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<ll>> dp(n + 1, vector<ll>(501, 0));
        dp[0][0] = 1;

        unordered_map<int, int> um;
        for (auto req : requirements)   um[req[0] + 1] = req[1];

        lp(i,1,n){
            lp(j,0,500){
                lpp(k,0,i){
                    if (j - k < 0)  continue;
                    dp[i][j] = (dp[i][j] + dp[i - 1][j-k]) % m;
                }
            }

            if (um.find(i) == um.end()) continue;

            int tmp = um[i];
            lp(j,0,500){
                if (j == tmp)   continue;
                dp[i][j] = 0;
            }
        }

        ll ans = 0;
        for (auto it : dp[n])   ans = (ans + it) % m;

        return ans;
    }
};
