class Solution {
public:
    const int MD = 1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {

        const int mxi = 800;
        vector<vector<long long>> myv(n + 1, vector<long long>(mxi + 1, 0));
        unordered_map<int, int> mp;



        for (auto i : reqs)  mp[i[0] + 1] = i[1];

        myv[0][0] = 1;

        long opt = 0;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= mxi; j++) {
                for (int p = 0; p < i; p++) {
                    if (j-p >= 0) {
                        myv[i][j] = (myv[i][j] + myv[i - 1][j-p]) % MD;
                    }
                }
            }

            if (mp.count(i)) {
                for (int k = 0; k <= mxi; k++) {
                    if (k != mp[i]) {
                        myv[i][k] = 0;
                    }
                }
            }
        }


        for (long ptr : myv[n]) {
            opt = (ptr + opt) % MD;
        }

        return opt;
    }
};
