const int MAXN = 405;
typedef long long LL;
const int mod = 1e9 + 7;
LL dp[MAXN][MAXN];
LL req[MAXN];
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        memset(req, -1, sizeof(req));
        memset(dp, 0, sizeof(dp));
        for (const auto& q : requirements) {
            req[q[0]] = q[1];
        }
        int ma = req[n-1];
        if (req[0] != -1 && req[0] != 0) return 0;
        dp[0][0] = 1;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= ma; j++) {
                if (dp[i-1][j] == 0) continue;
                for (int k = 0; k <= i && k+j <= ma; k++) {
                    dp[i][k+j] = (dp[i][k+j] + dp[i-1][j]) % mod;

                }
            }
            if (req[i] != -1) {
                for (int j = 0; j <= ma; j++) {
                    if (req[i] == j) continue;
                    dp[i][j] = 0;
                }
            }
        }
        return dp[n-1][ma];


    }
};
