class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
        int MOD=1e9+7, maxInv=400;
        unordered_map<int,int> mp;
        for(auto it : reqs) {
            mp[it[0]+1] = it[1];
        }

        vector<vector<long int>> v(n+1,vector<long int>(maxInv+1,0));
        v[0][0] = 1;

        for(int i=1;i<=n;i++) {
            for (int j=0;j<=maxInv;j++) {
                for (int k=0;k<i;k++) {
                    int prevInv = j-k;
                    if (prevInv>=0) {
                        v[i][j] = (v[i][j]+v[i-1][prevInv])%MOD;
                    }
                }
            }

            if (mp.find(i)!=mp.end()) {
                int targetInv = mp[i];
                for (int j=0;j<=maxInv;j++) {
                    if (j!=targetInv) {
                        v[i][j] = 0;
                    }
                }
            }
        }

        long int ans = 0;
        for (long int c : v[n]) {
            ans = (ans+c)%MOD;
        }

        return ans;
    }
};
