class Solution {
public:
    #define mod 1000000007
    #define ll long long
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        unordered_map<ll, ll> mpp;
        vector<vector<ll>>dp(n + 1, vector<ll>(400+10, 0));
        dp[0][0] = 1;

        for (auto& i : r) {
            mpp[i[0]+1] = i[1];
        }

        for (int i=1; i<=n; i++) {
            for (int j=0; j<=400; j++) {
                for (int k=0; k<i; k++) {
                    ll p = j-k;
                    if (p>= 0) {
                        dp[i][j] = (dp[i][j]+dp[i-1][p])%mod;
                    }
                }
            }

            if (mpp.count(i)) {
                ll tt = mpp[i];
                for (int j=0; j<=400; j++) {
                    if (j!=tt) {
                        dp[i][j] = 0;
                    }
                }
            }
        }

        ll ans = 0;
        for (auto& tt : dp[n]) {
            ans += tt;
        }

        return ans;
    }
};
