#define vll vector<long long>
#define max3(a,b,c) max(max((a),(b)),(c))
#define max4(a,b,c,d) max(max((a),(b)),max((c),(d)))
#define min3(a,b,c) min(min((a),(b)),(c))
#define min4(a,b,c,d) min(min((a),(b)),min((c),(d)))
#define allr(v) (v).rbegin(),(v).rend()

const int MOD = 1e9 + 7;

class Solution {
public:
    void f(int n, map<ll, ll>& mp, vvl &dp) {
        for (int i = 1; i <= n; ++i) {
            for (int v = 0; v <= 400; ++v) {
                for (int k = 0; k < i; ++k) {
                    if (v >= k) {
                        dp[i][v] = (dp[i][v] + dp[i - 1][v-k]) % MOD;
                    }
                }
            }
            if (mp.find(i) != mp.end()) {
                int re = mp[i];
                for (int v = 0; v <= 400; ++v) {
                    if (v != re) {
                        dp[i][v] = 0;
                    }
                }
            }
        }
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vvl dp(n + 1, vll(405, 0));
        dp[0][0] = 1;
        map<ll, ll> mp;
        for (auto& req : requirements) {
            mp[req[0] + 1] = req[1];
        }
        f(n, mp, dp);
        ll ans = 0;
        for (int v = 0; v <= 400; ++v) {
            ans = (ans + dp[n][v]) % MOD;
        }
        return ans;
    }
};
