    int numberOfPermutations(int n, vector<vector<int>>& r) {
        sort(begin(r),end(r));
        unordered_map<int,int> mp;
        for(auto i:r){
            mp[i[0]]=i[1];
        }
        vector<vector<ll>> dp(n+1,vector<ll>(401));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<401;j++){
                for(int k=0;k<i;k++){
                    int c=j-k;
                    if(j>=k){
                        dp[i][j]+=dp[i-1][c];
                        dp[i][j]%=mod;
                    }
                }
            }
            if(mp.count(i-1)){
                for(int j=0;j<401;j++){
                    if(mp[i-1]!=j){
                        dp[i][j]=0;
                    }
                }
            }
        }
        ll ans=0;
        for(int i=0;i<401;i++){
            ans+=dp[n][i];
            ans%=mod;
        }
        return ans;
    }
