class Solution {
public:
      int mod = 1e9 +7;
      int MAXI = 400;



    int numberOfPermutations(int n, vector<vector<int>>& req) {

                vector<vector<long long>> dp(n + 1, vector<long long>(MAXI + 1, 0));
        unordered_map<int, int> map;


        for (int  i =0; i< req.size() ;i++) {

            map[req[i][0] + 1] = req[i][1];

        }








        dp[0][0] = 1;



        for (int l = 1; l <= n; l++) {


            for (int i = 0; i <= MAXI; i++) {


                for (int p = 0; p < l; p++) {


                    int pI = i - p;


                    if (pI >= 0) {


                        dp[l][i] = (dp[l][i] + dp[l - 1][pI]) % mod;


                    }


                }


            }



            if (map.count(l)) {
                int tI = map[l];


                for (int i = 0; i <= MAXI; i++) {


                    if (i != tI) {


                        dp[l][i] = 0;


                    }
                }
            }
        }

        long long ans = 0;


        for (long long c : dp[n]) {


            ans = (ans + c) % mod;


        }

        return static_cast<int>(ans);
    }
};
