#define ll long long int

const ll mod = 1e9 + 7;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {

        vector<ll> a (n + 1, -1);

        for(ll i = 0; i<r.size(); i++){
            a[r[i][0]] = r[i][1];
        }

        ll N = a[n - 1];

        vector<vector<ll>> dp (n + 1, vector<ll> (N + 5));

        dp[0][0] = 1;
        dp[0][1] = -1;

        for(ll i = 0; i<=n - 1; i++){

            for(ll j = 1; j<=N; j++){
                dp[i][j] += dp[i][j - 1];
                dp[i][j] %= mod;
                dp[i][j] += 2 * mod;
                dp[i][j] %= mod;
            }

            if(a[i] != -1){
                for(ll j = a[i]; j<a[i] + 1; j++){
                    dp[i + 1][j] += dp[i][j];
                    dp[i + 1][min(N + 1, j + i + 2)] -= dp[i][j];
                }
            }
            else{
                for(ll j = 0; j<=N; j++){
                    dp[i + 1][j] += dp[i][j];
                    dp[i + 1][min(N + 1, j + i + 2)] -= dp[i][j];
                }
            }
        }

        return dp[n - 1][N];
    }
};
