class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        unordered_map<int, int> aa;
        for (auto hi : req) aa[hi[0] + 1] = hi[1];
         int mod = 1'000'000'007;
         int lim = 400;
        vector<vector<long long>> bb(n + 1, vector<long long>(lim + 1, 0));
        bb[0][0] = 1;
        for (int i = 1; i <= n; ++i) {
            for (int j = 0; j <= lim; ++j) {
                for (int ee = 0; ee < i; ++ee) {
                    int ff = j - ee;
                    if (ff >= 0) bb[i][j] = (bb[i][j] + bb[i - 1][ff]) % mod;
                }
            }
            if (aa.find(i) != aa.end()) {
                int biee = aa[i];
                for (int j = 0; j <= lim; ++j)
                    if (j != biee) bb[i][j] = 0;
            }
        }
        long long ans = 0;
        for (long long j : bb[n]) ans = (ans + j) % mod;
        return ans;
    }
};
