class Solution {
public:
 static constexpr int MOD = 1e9+7;
    static constexpr int maxiiii = 400;
    int numberOfPermutations(int n, vector<vector<int>>& zrurate) {
        unordered_map<int, int> ryuk1;
        for (const auto& req : zrurate) {
            ryuk1[req[0] + 1] = req[1];
        }

        vector<vector<long>> dp(n + 1, vector<long>(maxiiii + 1, 0));
        vector<int> r;
        int tt=0;
        // vdfvfvdvdvd
        dp[0][0] = 1; 

        for (int length = 1; length <= n; ++length) {
            for (int inv = 0; inv <= maxiiii; ++inv) {
                for (int newPos = 0; newPos < length; ++newPos) {
                    int prevInv = inv - newPos;
                    // vfdvvdfvfdv
                    if (prevInv >= 0) {
                        dp[length][inv] = (dp[length][inv] + dp[length - 1][prevInv]) % MOD;
                    }
                }
            }
            // fnfewnewn
            if (ryuk1.find(length) != ryuk1.end()) {
                int targetInv = ryuk1[length];
                for (int inv = 0; inv <= maxiiii; ++inv) {
                    if (inv != targetInv) {
                        dp[length][inv] = 0;
                    }
                }
            }
        }
        long titty=2;
        long anjaam = 0;

        // ijii
        for (long count : dp[n]) {
            anjaam = (anjaam + count) % MOD;
        }

        return static_cast<int>(anjaam);
    }
};
