#define ll long long
class Solution {
public:
    int N;
    int mod=1e9+7;
    vector<vector<ll>>dp;
    int add_mod(ll x,ll y){
        return ((x%mod+y%mod)%mod);
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int mx = 400;
        vector<int> reqMap(400, -1);
        sort(requirements.begin(),requirements.end());
        for (auto& req : requirements) {
            reqMap[req[0]] = req[1];
        }
        N=n;

        vector<vector<ll>> dp(400, vector<ll>(500, 0));
        dp[0][0] = 1;

        for (int i = 1; i <= N; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int newPos = 0; newPos < i; newPos++) {
                    int prevInv = j - newPos;
                    if (prevInv >= 0 && i-1>=0) {
                        dp[i][j] = add_mod(dp[i][j], dp[i - 1][prevInv]);
                    }
                }
            }

            if (reqMap[i - 1] != -1) {
                int targetInv = reqMap[i - 1];
                for (int j = 0; j <= mx; j++) {
                    if (j != targetInv) {
                        dp[i][j] = 0;
                    }
                }
            }
        }

        ll result = 0;
        for(int i=0;i<dp[n].size();i++){
            result = add_mod(result, dp[n][i]);

        }


        return (result);
    }
};
