class Solution {
    #define ll long long
    const int M=1e9+7;
public:
//     int solve(int sum,vector<int>&res,int req,int si,int idx,int prev){
//         if(sum>req) return 0;


//     }
    int numberOfPermutations(int n, vector<vector<int>>&r) {
        vector<ll int> dp(n+2,-1);
        //dp[0]=0;
        vector<pair<ll int,ll int>> vp;
        for(int i=0;i<r.size();i++) dp[r[i][0]]=r[i][1];
        for(int i=0;i<r.size();i++) vp.push_back({r[i][0],r[i][1]});
        sort(vp.begin(),vp.end());

        int prev=dp[0];
        ll int start=0,ans=0;
        //if(dp[0]!=0) return 0;
        if(vp[0].first==0) start++;
        for(int i=start;i<vp.size();i++){
            int req=vp[i].second-prev;
            int diff=vp[i].first+1;
            if(i>0) diff-=(vp[i-1].first+1);
            //if(req>diff) return 0;
            if(diff==1&&req==1){ans++;continue;}

        }
        ans=0;
        vector<vector<ll int>> v(n+1,vector<ll int>(402,0ll));
        v[0][0]=1;
        for(int i=0;i<n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<i+1;k++){
                    if(j>=k){
                        v[i+1][j]=(v[i+1][j]%M+v[i][j-k]%M)%M;
                    }
                }
            }
            if(dp[i]!=-1){
                for(int j=0;j<=400;j++){
                    if(j!=dp[i]) v[i+1][j]=0;
                }
            }
        }
        ans=0;
        for(ll int i=0;i<=400;i++){
            ans=(ans%M+v[n][i]%M)%M;
        }
        return ans;

    }
};
