class Solution {
public:
    const int SMOD = 1e9+7;
    const int SMAXI = 400;
    int numberOfPermutations(int sn,vector<vector<int>>& nums){
        long long sahilres = 0;
        unordered_map<int, int> smap;
        for (auto& it : nums) {
            smap[it[0] + 1] = it[1];
        }
        vector<vector<long>> sahildp(sn+1,vector<long>(SMAXI+1,0));
        sahildp[0][0]=1;
        for (int sahill=1;sahill<=sn;sahill++) 
        {
            for (int sahili=0;sahili<=SMAXI;sahili++) 
            {
                for (int sahilpos=0;sahilpos<sahill;sahilpos++) 
                {
                    int prevsahili = sahili - sahilpos;
                    if (prevsahili >= 0) 
                    {
                        sahildp[sahill][sahili]=(sahildp[sahill][sahili]+sahildp[sahill-1][prevsahili])%SMOD;
                    }
                }
            }
            if (smap.find(sahill)!=smap.end()) 
            {
                int targetsahili=smap[sahill];
                for (int sahili=0;sahili<=SMAXI;sahili++) 
                {
                    if (sahili != targetsahili) 
                    {
                        sahildp[sahill][sahili] = 0;
                    }
                }
            }
        }
        for (auto it:sahildp[sn]) 
        {
            sahilres=(sahilres+it)%SMOD;
        }
        return sahilres;
    }
};
