#define ll long long 
#define mod 1000000007
class Solution {
public:
    ll add(ll a,ll b){
        return (a%mod + b%mod) % mod;
    }

    ll dp[301][401];
    ll help(ll ind,ll k,vector<int>& cnt){
        if(cnt[ind]!=-1 && (cnt[ind]>k || cnt[ind]<k)){
            return 0;
        }
        if(ind==0){
            return (k==0);
        }
        if(dp[ind][k]!=-1){
            return dp[ind][k];
        }
        ll ans=0;
        for(ll i=0;i<=k;i++){
            if(i<=ind)
            ans=add(ans,help(ind-1,k-i,cnt));
        }
        return dp[ind][k]=ans;
    }
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        sort(req.begin(),req.end());
        for(int i=1;i<req.size();i++){
            if(req[i][1]<req[i-1][1]){
                return 0;
            }
        }
        vector<int> cnt(n,-1);
        for(int i=0;i<req.size();i++){
            cnt[req[i][0]]=req[i][1];
        }
        // for(int i=0;i<n;i++){
        //     cout<<cnt[i]<<" ";
        // }
        memset(dp,-1,sizeof(dp));
        ll ans=help(n-1,cnt[n-1],cnt);
        return ans;
    }
};
