class Solution {
public:
    const long long mod=1e9+7;
    long long func(int s,int e,int k){
    long long n=e-s+1;
    vector<long long> a(n);
    vector<long long> dp(k+1,0),dq(k+1,0);
    for(int i=0;i<n;i++){
        a[i]=s+i;
    }
    dp[0]=1;
    for(int i=0;i<n;i++){
        for(int j=1;j<=k;j++) {dp[j]=dp[j-1]+dp[j]; dp[j]%=mod;}
        dq[0]=1;
        for(int j=1;j<=k;j++){
            if(j-a[i]-1<0) dq[j]=dp[j];
            else dq[j]=dp[j]-dp[j-a[i]-1];  
            if(dq[j]<0) dq[j]+=mod;
            dq[j]%=mod;
        }
        swap(dp,dq);
    }
    return dp[k]%mod;
    }
    int numberOfPermutations(int n, vector<vector<int>>& a) {
        sort(a.begin(),a.end());
        int m=a.size();
        long long ans=1;
        int x=0,s=0;
        for(int i=0;i<m;i++){
            long long temp=func(s,a[i][0],a[i][1]-x);
            ans*=temp; ans%=mod;
            x=a[i][1];
            s=a[i][0]+1;
        }
        return (int)ans;
    }
};
