class Solution {
public:
    int mod = 1000000007;
    int MAX = 400;

    #define ll long long int

    int numberOfPermutations(int n, vector<vector<int>>& req) {
        unordered_map<int, int> mp;
        int m=req.size();

        for (int i=0;i<m;i++) {
            int val1=req[i][0];
            int val2=req[i][1];

            mp[val1+1] = val2;
        }

        ll sum=0;
        vector<ll> v(m);

        for(int i=0;i<m;i++)
        {
            sum+=req[i][1];
            v[i]=sum;
        }

        vector<vector<ll>> dp(n + 1, vector<ll>(MAX + 1, 0));
        dp[0][0] = 1;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= MAX; j++) {
                ll newj=0;

                while(newj<i)
                {
                    if ((j-newj) >= 0) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][j-newj]) % mod;
                    }
                    newj++;
                }
            }

            if (mp.count(i)) {
                int targetInv = mp[i];
                for (int j = 0; j <= MAX; j++) {
                    if (j != targetInv) {
                        dp[i][j] = 0;
                    }
                }
            }
        }

        ll ans = 0;
        for (int i=0;i<=MAX;i++) {
            ans = (ans + dp[n][i]) % mod;
        }

        return (int)(ans);
    }
};
