class Solution {
public:
    const int mod = 1e9 + 7;
    const int kitna = 405;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> values(n + 1, -1);
        for (auto i : requirements) values[i[0] + 1] = i[1];
        vector<vector<long long>> dp(n + 1, vector<long long>(kitna + 1, 0));
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= kitna; j++)
                for (int k = 0; k < i; k++)
                    if (j - k >= 0) (dp[i][j] += dp[i - 1][j - k]) %= mod;
            if (values[i] >= 0) {
                int kon = values[i];
                for (int x = 0; x <= kitna; x++) if (x != values[i]) dp[i][x] = 0;
            }
        }
        long long ans = 0;
        for (int i = 0; i <= kitna; i++) (ans += dp[n][i]) %= mod;
        return ans;
    }
};
